-- 创建数据库werewolf

CREATE DATABASE werewolf DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
ALTER DATABASE werewolf CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci;

-- stoken
create table token
(
    tokenID nvarchar(100) primary key comment '根据用户ID和时间生成的token',
    userID nvarchar(100) comment '用户ID',
    createtime datetime comment 'token被创建的时间',
    validity long comment 'token的有效时长'
);

--创建表userInfo,用户列表
create table imgPath
(
    imgPathID nvarchar(100) primary key comment '主键',
    path nvarchar(1000) comment '图片路径',
    foreignID nvarchar(100) comment '关联ID'
);

-- 管理员信息
create table adminInfo(
    adminID nvarchar(100) primary key comment '管理员ID',
    adminName nvarchar(100) comment '管理员用户名',
    tel nvarchar(100) comment '手机号',
	adminPW nvarchar(100) comment '管理员密码'
);


--创建表userInfo,用户列表
create table userInfo
(
    userID nvarchar(100) primary key comment '主键',
    openID nvarchar(100) comment '微信小程序用户ID',
    userName nvarchar(100) comment '用户名，昵称',
    gender smallint comment '性别',
    createTime datetime comment '创建时间',
    latitude float default 32.03 comment '纬度',
    longitude float default 118.46 comment '经度',
    userType int comment '身份, 1 普通用户, 2 商家, 3 法官'
);



--创建表club, 俱乐部表
create table club
(
    clubID nvarchar(100) primary key comment '主键',
    clubName nvarchar(100) comment '俱乐部名称',
    creatorName nvarchar(100) comment '俱乐部创建者',
    userID nvarchar(100) comment '用户ID',
    provinceID nvarchar(100) default '-1' comment '省份ID',
    cityID nvarchar(100) default '-1' comment '城市ID',
    countyID nvarchar(100) default '-1' comment '区县ID',
    area text comment '详细地址',
    longitude float default 118.46 comment '经度',
    latitude float default 32.03 comment '纬度',
    createTime datetime comment '创建时间'
);

alter table  club add constraint club_FK_province foreign key(provinceID) references province(provinceID);
alter table  club add constraint club_FK_city foreign key(cityID) references city(cityID);
alter table  club add constraint club_FK_county foreign key(countyID) references county(countyID);
alter table  club add constraint club_FK_userInfo foreign key(userID) references userInfo(userID);

--province，省份
create table province
(
    provinceID nvarchar(100) primary key comment '主键',
    provinceName nvarchar(100) comment '省份名称'
);

