--judge，法官
create table judge
(
    judgeID nvarchar(100) primary key comment '主键',
    userID nvarchar(100) comment '用户名，昵称',
    step smallint comment '法官申请审核状态，１表示申请，２表示申请成功，３表示被驳回',
    description text comment '驳回原因',
    createTime datetime comment '申请时间',
    reviewTime datetime comment '批准时间'
);

alter table  judge add constraint judge_FK_userInfo foreign key(userID) references userInfo(userID);


--activity，活动
create table activity
(
    activityID nvarchar(100) primary key comment '主键',
    userID nvarchar(100) comment '创建者',
    entryStartDate date comment '报名开始时间',
    entryEndDate date comment '报名截止时间',
    startDate date comment '比赛开始时间',
    endDate date comment '比赛截止时间',
    fee nvarchar(100)  comment '报名费用',
    activityName nvarchar(100) comment '活动标题',
    minNumber  int comment '最少参加活动人数',
    maxNumber  int comment '最多参加活动人数',
    typeID smallint comment '区分活动类型，欢乐局为１，　俱乐部为２',
    clubID  nvarchar(100) comment '预约俱乐部ID',
    description text comment '活动要求',
    area text comment '活动地址',
    createTime datetime comment '创建时间',
    participateState tinyint(1) comment '是否可以申请活动，　true 表示可以继续申请'
);
alter table  activity add constraint activity_FK_club foreign key(clubID) references club(clubID);
alter table  activity add constraint activity_FK_userInfo foreign key(userID) references userInfo(userID);

--activity，活动
create table participate
(
    participateID nvarchar(100) primary key comment '主键',
    userID nvarchar(100) comment '加入者ID',
    activityID nvarchar(100) comment '活动ID',
    createTime datetime comment '加入时间'
);
alter table  participate add constraint participate_FK_activity foreign key(activityID) references activity(activityID);
alter table  participate add constraint participate_FK_userInfo foreign key(userID) references userInfo(userID);


